import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function PaymentForm() {
  const [paymentData, setPaymentData] = useState({
    pan: "",
    holder: "",
    expire: "",
    cvv2: "",
  });

  const handleChange = (event) => {
    setPaymentData({ ...paymentData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post("/create-payment", paymentData);

      if (response.data.status === 200) {
        // Redirect to the cheque page
        window.location.href = "/cheque";
      } else {
        console.error("Error occurred during payment processing");
      }
    } catch (error) {
      console.error("Error occurred during payment processing", error);
    }
  };

  return (
    <div className="App">
      <h1>Payment Form</h1>
      <form onSubmit={handleSubmit}>
        <label>
          PAN:
          <input
            type="text"
            name="pan"
            value={paymentData.pan}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Cardholder Name:
          <input
            type="text"
            name="holder"
            value={paymentData.holder}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Expiration Date:
          <input
            type="text"
            name="expire"
            value={paymentData.expire}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          CVV2:
          <input
            type="text"
            name="cvv2"
            value={paymentData.cvv2}
            onChange={handleChange}
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <Switch>
        <Route exact path="/" component={PaymentForm} />
        <Route path="/cheque" component={Cheque} />
      </Switch>
    </div>
  );
}

export default App;
